import { Redirect } from "react-router-dom";
import React from "react";
// Service to check authentication for user and to signOut
const Auth = {
  signOut() {
    localStorage.clear();
    return(
      <Route
      {...rest}
      render={props =>
        Auth.isAuth() ? <Needy {...props} /> : <Redirect to={`/`} />
      }
    />
      )
  },
  isAuth(){
    if(localStorage.token){
        return true;
    } else {
        return false;
    }
}
};
export default Auth;
